def typing_effect_line(line, y, start_time):
    oled.fill(0) 

    if int(time.time()) % 2 == 0: 
        oled.fill_rect(morse_icon_x, morse_icon_y, morse_icon_width, HEIGHT - morse_icon_y, 1) 
        oled.fill_rect(morse_icon_x + morse_icon_width + morse_icon_dot_spacing, morse_icon_y, morse_icon_dot_size, morse_icon_dot_size, 1) 
        oled.fill_rect(morse_icon_x + morse_icon_width + morse_icon_dot_spacing, morse_icon_y + morse_icon_dot_size + morse_icon_dot_spacing, morse_icon_dot_size, morse_icon_dot_size, 1) 

    for i in range(len(line) + 1):
        oled.text(line[:i], (WIDTH - len(line) * 8) // 2, y) 

        elapsed_time = time.time() - start_time
        hours = int(elapsed_time // 3600)
        minutes = int((elapsed_time % 3600) // 60)
        seconds = int(elapsed_time % 60)
        timer_text = "{:02}:{:02}:{:02}".format(hours, minutes, seconds)

        oled.text(timer_text, WIDTH - len(timer_text) * 8, HEIGHT - 10)
        oled.text("ver 0.11", WIDTH - len("ver 0.11") * 8, HEIGHT - 1)

        oled.show()
        time.sleep(0.2) 

def typing_effect(line1, line2, start_time):
    typing_effect_line(line1, HEIGHT // 2 - 12, start_time)
    time.sleep(1) 
    typing_effect_line(line2, HEIGHT // 2, start_time)

def display_logo():
    oled.fill(0)
    for y, row in enumerate(pixel_logo):
        for x, pixel in enumerate(row):
            oled.pixel(x, y, pixel)
    oled.show()
    time.sleep(1)

def display_blinking_cursor(text, start_time):
    cursor_pos = len(text) * 8 
    while True:
        oled.fill(0)
        oled.text(enter_text_message, (WIDTH - len(enter_text_message) * 8) // 2, HEIGHT // 2 - 12)
        oled.text(enter_text_message_line2, (WIDTH - len(enter_text_message_line2) * 8) // 2, HEIGHT // 2)

        elapsed_time = time.time() - start_time
        hours = int(elapsed_time // 3600)
        minutes = int((elapsed_time % 3600) // 60)
        seconds = int(elapsed_time % 60)
        timer_text = "{:02}:{:02}:{:02}".format(hours, minutes, seconds)

        oled.text(timer_text, WIDTH - len(timer_text) * 8, HEIGHT - 10)
        oled.text("ver 0.1", WIDTH - len("ver 0.1") * 8, HEIGHT - 1)

        if int(time.time()) % 2 == 0:
            oled.text("_", (WIDTH - cursor_pos) // 2, HEIGHT // 2 + 8) 
        oled.show()
        time.sleep(0.5)

def display_morse_input(input_value, start_time):
    text = ''.join(['.' if ch == '0' else '-' for ch in input_value])
    display_blinking_cursor(text, start_time)

def translate_morse(input_value):
    translated_text = ''
    morse_letter = ''.join(input_value)
    if morse_letter in morse_dict:
        translated_text = morse_dict[morse_letter]
    return translated_text

