def typing_effect_line(line, y, start_time):
    oled.fill(0)  # Clear the display

    for i in range(len(line) + 1):
        oled.text(line[:i], (WIDTH - len(line) * 8) // 2, y)  
        elapsed_time = time.time() - start_time
        hours = int(elapsed_time // 3600)
        minutes = int((elapsed_time % 3600) // 60)
        seconds = int(elapsed_time % 60)
        
        if hours > 0:
            timer_text = "{:00}:{:00}:{:00}".format(hours, minutes, seconds)
        elif minutes > 0:
            timer_text = "{:00}:{:00}".format(minutes, seconds)
        else:
            timer_text = "{:00}".format(seconds)
        oled.text(timer_text, WIDTH - len(timer_text) * 8, HEIGHT - 10)
        oled.text(version, 0, HEIGHT - 10)

        oled.show()
        time.sleep(0.2)  


def typing_effect(line1, line2, start_time):
    typing_effect_line(line1, HEIGHT // 2 - 20, start_time)
    time.sleep(1)  
    typing_effect_line(line2, HEIGHT // 2 - 8, start_time)


def display_logo():
    oled.fill(0)
    for y, row in enumerate(pixel_logo):
        for x, pixel in enumerate(row):
            oled.pixel(x, y, pixel)
    oled.show()
    time.sleep(5)  

